function getShuffledDeck() {
  const deck = Array.from({ length: 78 }, (_, i) => ({
    number: i + 1,
    reversedProbability: Math.random() * 0.5
  }));

  for (let i = deck.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [deck[i], deck[j]] = [deck[j], deck[i]];
  }

  return deck;
}

function getCard(cardNumber, reversedProbability, include_reversed) {
  const majorArcana = [
    { name: "The Fool" }, { name: "The Magician" }, { name: "The High Priestess" },
    { name: "The Empress" }, { name: "The Emperor" }, { name: "The Hierophant" },
    { name: "The Lovers" }, { name: "The Chariot" }, { name: "Strength" },
    { name: "The Hermit" }, { name: "Wheel of Fortune" }, { name: "Justice" },
    { name: "The Hanged Man" }, { name: "Death" }, { name: "Temperance" },
    { name: "The Devil" }, { name: "The Tower" }, { name: "The Star" },
    { name: "The Moon" }, { name: "The Sun" }, { name: "Judgement" },
    { name: "The World" }
  ];

  const minorArcana = [
    { suit: "Wands", number: "Ace" }, { suit: "Wands", number: "Two" }, { suit: "Wands", number: "Three" },
    { suit: "Wands", number: "Four" }, { suit: "Wands", number: "Five" }, { suit: "Wands", number: "Six" },
    { suit: "Wands", number: "Seven" }, { suit: "Wands", number: "Eight" }, { suit: "Wands", number: "Nine" },
    { suit: "Wands", number: "Ten" }, { suit: "Wands", number: "Page" }, { suit: "Wands", number: "Knight" },
    { suit: "Wands", number: "Queen" }, { suit: "Wands", number: "King" }, { suit: "Cups", number: "Ace" },
    { suit: "Cups", number: "Two" }, { suit: "Cups", number: "Three" }, { suit: "Cups", number: "Four" },
    { suit: "Cups", number: "Five" }, { suit: "Cups", number: "Six" }, { suit: "Cups", number: "Seven" },
    { suit: "Cups", number: "Eight" }, { suit: "Cups", number: "Nine" }, { suit: "Cups", number: "Ten" },
    { suit: "Cups", number: "Page" }, { suit: "Cups", number: "Knight" }, { suit: "Cups", number: "Queen" },
    { suit: "Cups", number: "King" }, { suit: "Swords", number: "Ace" }, { suit: "Swords", number: "Two" },
    { suit: "Swords", number: "Three" }, { suit: "Swords", number: "Four" }, { suit: "Swords", number: "Five" },
    { suit: "Swords", number: "Six" }, { suit: "Swords", number: "Seven" }, { suit: "Swords", number: "Eight" },
    { suit: "Swords", number: "Nine" }, { suit: "Swords", number: "Ten" }, { suit: "Swords", number: "Page" },
    { suit: "Swords", number: "Knight" }, { suit: "Swords", number: "Queen" }, { suit: "Swords", number: "King" },
    { suit: "Pentacles", number: "Ace" }, { suit: "Pentacles", number: "Two" }, { suit: "Pentacles", number: "Three" },
    { suit: "Pentacles", number: "Four" }, { suit: "Pentacles", number: "Five" }, { suit: "Pentacles", number: "Six" },
    { suit: "Pentacles", number: "Seven" }, { suit: "Pentacles", number: "Eight" }, { suit: "Pentacles", number: "Nine" },
    { suit: "Pentacles", number: "Ten" }, { suit: "Pentacles", number: "Page" }, { suit: "Pentacles", number: "Knight" },
    { suit: "Pentacles", number: "Queen" }, { suit: "Pentacles", number: "King" }
  ];

  if (cardNumber <= 22) {
    return { name: majorArcana[cardNumber - 1].name, reversed: include_reversed && Math.random() < reversedProbability };
  } else {
    const suitIndex = Math.floor((cardNumber - 23) / 14);
    const numberIndex = (cardNumber - 23) % 14;
    const suit = ["Wands", "Cups", "Swords", "Pentacles"][suitIndex];
    return {
      name: `${minorArcana[numberIndex].number} of ${suit}`,
      reversed: include_reversed && Math.random() < reversedProbability
    };
  }
}

